import React, { useState, useEffect } from "react";

export const GifGrids = ({category}) =>{

    const [images, setImages] = useState([]); 

    useEffect(() => {
        getGifs();
    }, [])


    const getGifs = async() => {

        const url = 'https://api.giphy.com/v1/gifs/search?q=Rick+and+Morty&api_key=JC3j7ybaaioNCE6Scagbyce0tSwE2n0y';

        const resp = await fetch(url);
        const { data } = await resp.json();                       

        const gifs = data.map(img => {
            return {
                id: img.id,
                title: img.title,
                url: img.images.downsized_medium.url
            }
        });



        setImages(gifs)
    }


    return (
        <div>
            <h3>{category}</h3>
            {console.log(images)}
            <ul>
                {
                    images.map((gif) => {
                        return <li key={gif.id}>{ gif.title }</li>
                    }

                    )
                }
            </ul>
        </div>
    )
}
