import { useState, useEffect } from "react";
import { getGifs } from "../helpers/getGifs";

export const useFetchGifs = ( category ) => {
     const [state, setstate] = useState(
         {
             data: [],
             loading: true
         }
     );

     useEffect( () => {

        getGifs( category )
            .then( imgs => {
                console.log(imgs)
                setTimeout(() => {
                    setstate({
                        data: imgs,
                        loading: false
                    })    
                }, 3000);

            })

     }, []);

     return state;
}
