    //     setCategories( cats =>  [  ...categories, 'Super campeones']) //SEGUNDA FORMA
    // }

    console.log(categories)

    return (
        <>
            <h2>Gif React App</h2>
            <AddCategory setCategories={ setCategories } />
            <hr />
            {/* <button onClick={handleAdd}>AGREGAR</button> */}

            <ul>
