import React, { useState, useEffect } from "react";
import { getGifs } from "../helpers/getGifs";

import { GifGridItem } from "./GifGridItem";

export const GifGrids = ({category}) =>{

    const [images, setImages] = useState([]); 

    useEffect(() => {
        getGifs(category)
            .then( imgs => {
                console.log(imgs)
                setImages(imgs)
            })
    }, [])


    //setImages(gifs)


    return (
        <div>
            <h3>{category}</h3> 

                {
                    images.map( img => (
                        <GifGridItem
                            key={img.id} 
                            {...img} />
                        )   
                    )
                }

        </div>
    )
}
